import React from 'react';

function SafeImage({ src, alt, className }) {
  const [hasError, setHasError] = React.useState(false);
  // Accept any non-empty src that starts with http, /, or is a data:image/ (do not require file extension)
  const isValidImage = src && (
    src.startsWith('http') ||
    src.startsWith('/') ||
    src.startsWith('data:image/')
  );
  if (hasError || !isValidImage) {
    return (
      <div className={className} style={{background:'#f3f4f6',display:'flex',alignItems:'center',justifyContent:'center',width:'100%',height:'100%'}}>
        <img src="/default-rental-image.jpg" alt="placeholder" style={{maxWidth:'60%',maxHeight:'60%',opacity:0.7}} />
      </div>
    );
  }
  return <img src={src} alt={alt} className={className} onError={()=>setHasError(true)} style={{width:'100%',height:'100%',objectFit:'cover'}} />;
}

export default SafeImage;
