import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import './theme.css';
import App from './App';
import { AuthProvider } from './Context/AuthContext'; // ✅ import the provider

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <AuthProvider> 
    <App />
  </AuthProvider>
);
